class Solution {
public:
    bool is_subsequence(string a, string b){
        string temp="";
        // cout<<a<<endl;
        int i=0;
        while(i<b.length()){
            temp+=a[i];
            i++;
        }
      
        if(temp==b)return true;
          // cout<<a.length()<<endl;
        while(i<a.length()){
            // cout<<temp<<endl;
            if(temp==b){
                return true;
            }
            temp+=a[i];
            temp.erase(temp.begin()+0);
            i++;
        }
        // cout<<temp<<endl;
        if(temp==b)return true;
        return false;
    }
    int repeatedStringMatch(string a, string b) {
        int count=1;
        string add=a;
        // bool flag=
        if(a=="a"  && b==""){
return -1;}
        while(a.length()<b.length()){
            if(2*a.length()<=b.length()){
                count+=((a.length())/add.length());
                a+=a;
                
            }else{
                a+=add;
                count++;
            }
            
            // cout<<a.length()<<endl;
            // count++;
        }
        // cout<<a;
        int j=count;
        // cout<<is_subsequence(a,b);
        if(is_subsequence(a,b)){
            return count;
        }
        else{
            // cout<<"hello"<<endl;
            while(j>0){
                
                a+=add;
                j--;
                count++;
                if(is_subsequence(a,b)){
                    return count;
                }
                
            }
            if(j==0){
                return -1;
            }
            
            // if(is_subsequence(a,b)){
            //     return count+1;
            // }
        }
        return count;
        
    }
};