class Solution {
public:
    bool all_is_equal(string ans){
        
        int i=1;
        while(i<ans.size()){
            if(ans[i]!=ans[i-1]){
                return false;
            }
            i++;
        }
        return true;
    }
    string longestPrefix(string s) {
        
        
        if(s==""){
            return "";
        }
        
        string ans="";
        if(s.length()==1){
            return ans;
        }
        int j=s.length()-1;
        int i=j-1;
        
        while(i>=0){
            if(s[i]==s[j]){
                ans+=s[i];
                i--;j--;
            }    
            else{
                if(s[i]==s[i+1]){
                   if(all_is_equal(ans)){
                        i--;
                        continue;
                    }
                }
                if(ans==""){
                    i--;
                }
                // i--;
                
                j=s.length()-1;
                ans="";
            }
        }
        reverse(ans.begin(),ans.end());
        return ans;
    }
};